import type { Metadata } from "next";
import Link from "next/link";

export const metadata: Metadata = {
  title: "Reset Password",
};

async function resetPasswordAction(formData: FormData): Promise<void> {
  "use server";

  const { redirect } = await import("next/navigation");
  const email = (formData.get("email") as string)?.trim().toLowerCase();

  if (!email) {
    redirect("/reset-password?error=email_required");
  }

  // Import inline to avoid exposing db to client bundle
  const { db } = await import("@/lib/db");

  const user = await db.user.findUnique({ where: { email } });

  if (user) {
    // Generate a secure token
    const tokenBytes = crypto.getRandomValues(new Uint8Array(32));
    const token = Array.from(tokenBytes)
      .map((b) => b.toString(16).padStart(2, "0"))
      .join("");

    const expires = new Date(Date.now() + 1000 * 60 * 60); // 1 hour

    await db.verificationToken.create({
      data: {
        identifier: email,
        token,
        expires,
      },
    });

    // TODO: send email with reset link
    // The reset link would be: `${process.env.NEXTAUTH_URL}/reset-password/confirm?token=${token}&email=${encodeURIComponent(email)}`
    // Wire up your email provider here (Resend, SendGrid, Nodemailer, etc.)
    console.log(
      `[reset-password] Token for ${email}: ${token} (expires ${expires.toISOString()})`
    );
  }

  // Always redirect to success to prevent email enumeration
  redirect("/reset-password?success=1");
}

